import React, { useEffect, useState } from 'react';
import { getLevel, getPumpStatus, pumpOn, pumpOff } from './services/api';
import './App.css';

const App = () => {
  const [nivel, setNivel] = useState(0);
  const [estadoBomba, setEstadoBomba] = useState('');

  const fetchData = async () => {
    const nivelData = await getLevel();
    const bombaData = await getPumpStatus();
    setNivel(nivelData.nivel_metros);
    setEstadoBomba(bombaData.motobomba);
  };

  useEffect(() => {
    fetchData();
    const interval = setInterval(fetchData, 5000);
    return () => clearInterval(interval);
  }, []);

  const handlePumpOn = async () => {
    await pumpOn();
    fetchData();
  };

  const handlePumpOff = async () => {
    await pumpOff();
    fetchData();
  };

  return (
    <div className="container">
      <img src="/pond.jpg" alt="Estanque" className="pond-image" />
      <div className="overlay">
        <h1>Nivel del estanque: {nivel} metros</h1>
        <h2>Motobomba: {estadoBomba}</h2>
        <button onClick={handlePumpOn} className="button-on">Encender Motobomba</button>
        <button onClick={handlePumpOff} className="button-off">Apagar Motobomba</button>
      </div>
    </div>
  );
};

export default App;
