import React, { useState, useEffect } from 'react';
import './App.css';

const App = () => {
  const [nivel, setNivel] = useState(2.5); // Nivel inicial, máximo 5

  useEffect(() => {
    const interval = setInterval(() => {
      const nuevoNivel = +(Math.random() * 5).toFixed(2);
      setNivel(nuevoNivel);
    }, 5000);

    return () => clearInterval(interval);
  }, []);

  return (
    <div className="contenedor">
      <h1>Nivel del Estanque: {nivel} m</h1>
      <div className="estanque-con-etiquetas">
        <div className="etiquetas">
          <span>100%</span>
          <span>80%</span>
          <span>60%</span>
          <span>40%</span>
          <span>20%</span>
          <span>0%</span>
        </div>
        <div className="estanque">
          <div
            className="agua"
            style={{ height: `${(nivel / 5) * 100}%` }}
          ></div>
        </div>
      </div>
    </div>
  );
};

export default App;
